generated.css:
header {
  height: 5rem;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 2px 2px 24px rgba(0, 0, 0, 0.125);
}
header img {
  align-self: center;
  margin-left: 15px;
}

ul {
  list-style: none;
  display: flex;
}
ul li {
  margin-right: 15px;
}

.text-center {
  text-align: center;
}

article {
  margin: 0 auto;
  width: 80vw;
}
article .card--container {
  display: flex;
  align-items: flex-start;
  box-shadow: 2px 2px 24px rgba(0, 0, 0, 0.125);
  margin: 0 20px;
}
article .card--container .card-video--container {
  width: auto;
  min-width: 800px;
}
article .card--container .card-donation-info {
  width: 100%;
  margin: 0 40px 0 0;
}
article h2 {
  font-size: 3rem;
  margin: 1rem 0 0;
}
article .card-video--container iframe {
  width: 100%;
}
article .card-video--container iframe #player.player {
  width: 100%;
}
article .card-donation-progress {
  height: 2rem;
  width: 100%;
  background-color: #dddddd;
  position: relative;
}
article .card-donation-progress .progress {
  position: absolute;
  top: 0;
  left: 0;
  width: 68%;
  height: 100%;
  background-color: #006E8E;
}
article .card-donation-button {
  background-color: #006E8E;
  height: 3rem;
  text-align: center;
  color: #fff;
  border-radius: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
}

@media screen and (max-width: 720px) {
  article .card--container {
    flex-direction: column;
  }
}
